package bucles;

public class instrucciones_de_salto {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
			while (true) {
				System.out.println("Bucle WHILE: 1");
				for (int i=0;i<3;i++) {
					System.out.println("Bucle FOR: "+ (i+1));
				}
				break;
			}
	}
}
